INSERT INTO `TOPIC`(`title`, `description`, `created_at`, `created_by`, `updated_at`, `updated_by`)
VALUES ('Politique rurale', 'La politique rurale joue un rôle crucial dans le développement équilibré des territoires et la préservation du tissu économique et social des zones rurales. Alors que les populations rurales sont confrontées à des défis tels que le déclin démographique, la dégradation des services publics et la perte d activités économiques, il est essentiel de mettre en œuvre des politiques efficaces pour revitaliser les campagnes et valoriser leurs atouts uniques.',
